package View;import ViewModel.MyViewModel;import javafx.application.Application;import javafx.fxml.FXMLLoader;import javafx.geometry.Pos;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.Button;import javafx.scene.image.Image;import javafx.scene.image.ImageView;import javafx.scene.layout.VBox;import javafx.stage.Stage;import java.io.FileInputStream;//todo: "eat" the solution?///todo: check running time  (generate & solve)/** * Main - init the Application (main stage) */public class Main extends Application {    @Override    public void start(Stage primaryStage) throws Exception{        FXMLLoader fxmlLoader = new FXMLLoader(getClass().getClassLoader().getResource("View/MyView.fxml"));        Parent root = fxmlLoader.load();        primaryStage.setTitle("Maze Game");        Scene scene = new Scene(root, 800, 600);        primaryStage.setScene(scene);        MyViewModel viewModel = MyViewModel.getInstance();        MyViewController view =fxmlLoader.getController();        viewModel.addObserver(view);        MyViewController MvController = fxmlLoader.getController();        MvController.setStageAndScene(primaryStage);        primaryStage.setOnCloseRequest(e->{ //listen to close event (user press on "x" key)            view.checkExitWanted();        });        primaryStage.show();    }    public static void main(String[] args) {        launch(args);    }}