package View;import algorithms.mazeGenerators.Maze;import algorithms.mazeGenerators.Position;import algorithms.search.AState;import algorithms.search.Solution;import javafx.beans.property.SimpleStringProperty;import javafx.scene.canvas.Canvas;import javafx.scene.canvas.GraphicsContext;import javafx.scene.control.Alert;import javafx.scene.control.Button;import javafx.scene.image.Image;import javafx.scene.paint.Color;import javafx.beans.property.StringProperty;import java.io.FileInputStream;import java.io.FileNotFoundException;import java.util.ArrayList;//make sure it extends Canvas of JAVA (not AWT)public class MazeDisplayer extends Canvas {    StringProperty imageFileNameWall = new SimpleStringProperty();    StringProperty imageFileNameWall2 = new SimpleStringProperty();    StringProperty imageFileNameWall3 = new SimpleStringProperty();    StringProperty imageFileNameWall4 = new SimpleStringProperty();    StringProperty imageFileNameWall5 = new SimpleStringProperty();    StringProperty imageFileNameAriel = new SimpleStringProperty();    StringProperty imageFileNameFlounder = new SimpleStringProperty();    StringProperty imageFileNameSebastian = new SimpleStringProperty();    StringProperty imageFileNameUrsula = new SimpleStringProperty();    StringProperty imageFileNameStartPosition = new SimpleStringProperty();    StringProperty imageFileNameGoalPosition = new SimpleStringProperty();    StringProperty imageFileNameSolution = new SimpleStringProperty();    StringProperty imageFileNameBackground = new SimpleStringProperty();    private String player;    private Maze mazeDisplay;    private int PlayerRow = 0;    private int PlayerCol = 0;    private Boolean drawSolution = false;    private Solution solution = null;    //constructor    public MazeDisplayer() {        widthProperty().addListener(e -> draw());        heightProperty().addListener(e -> draw());}    public void setPlayerPosition(int Row, int Col) {        PlayerRow = Row;        PlayerCol = Col;        draw(); //change the maze (draw again) because player moved    }    public void DrawWhenSolve(Solution ViewSolution){        solution=ViewSolution;        draw();    }    public void ChangeDrawSolution(Button btn) {        if(drawSolution){            btn.setText("Show Maze Solution");            drawSolution=false;}        else{            btn.setText("Hide Maze Solution");            drawSolution=true;}}    public void FalseDrawSolution(Button btn) {        drawSolution=false;        btn.setText("Show Maze Solution");}    private void draw() {        if(mazeDisplay != null){            //find the canvas sizes, and split into rows*cols cells            double canvasHeight = getHeight();            double canvasWidth = getWidth();            int rows = mazeDisplay.getRows();            int cols = mazeDisplay.getColumns();            double cellHeight = canvasHeight / rows;            double cellWidth = canvasWidth / cols;            //define where to draw over the canvas            GraphicsContext graphicsContext = getGraphicsContext2D();            //clear the canvas: (if we already draw over the canvas)            graphicsContext.clearRect(0, 0, canvasWidth, canvasHeight); //clearRect is the attribute that draw over the canvas            Image OceanImage = null;            try {                OceanImage = new Image(new FileInputStream(getImageFileNameBackground()));            } catch (FileNotFoundException e) {                ErrorMessage("There is no wall image");            }            if(OceanImage != null)                graphicsContext.drawImage(OceanImage, 0, 0, canvasWidth, canvasHeight);            drawMazeWalls(graphicsContext, rows, cols, cellHeight, cellWidth);            if(drawSolution)                if(solution==null) { //todo: check if needed (need to catch this no-solution exception before)                    InformationMessage("there is no solution to draw");                }                else                    drawSolution(graphicsContext, cellHeight, cellWidth);            drawMazePlayer(graphicsContext, cellHeight, cellWidth);            drawMazeStartAndGoal(graphicsContext, cellHeight, cellWidth);        }    }    private void drawSolution(GraphicsContext graphicsContext, double cellHeight, double cellWidth) {        Image solutionImage = null;        try {            solutionImage = new Image(new FileInputStream(getImageFileNameSolution()));        } catch (FileNotFoundException e) {            ErrorMessage("There is no image for the solution");        }        graphicsContext.setFill(Color.BLUE); //the color that we want to add in our draw        ArrayList<AState> solutionPath = solution.getSolutionPath();        for (int i = 0; i < solutionPath.size(); i++) {            String position = solutionPath.get(i).toString();            try {                if(position.equals(mazeDisplay.getStartPosition().toString()) || position.equals(mazeDisplay.getGoalPosition().toString())                        || position.equals((new Position(PlayerRow, PlayerCol)).toString())){                    continue;                }            } catch (Exception e) {                ErrorMessage("Error occurred while drawing solution function");            }            int index = position.indexOf(",");            String posX = position.substring(1,index);            String posY = position.substring(index+1, position.length()-1);            double x = Integer.parseInt(posX) * cellHeight;            double y = Integer.parseInt(posY) * cellWidth;            if(solutionImage== null)                graphicsContext.fillRect(y, x, cellWidth, cellHeight);            else                graphicsContext.drawImage(solutionImage, y, x, cellWidth, cellHeight);        }    }    private void drawMazePlayer(GraphicsContext graphicsContext, double cellHeight, double cellWidth) {        Image PlayerImage = null;        try {            switch (player){                case "Ariel" -> PlayerImage=new Image(new FileInputStream(getImageFileNameAriel()));                case "Ursula"-> PlayerImage=new Image(new FileInputStream(getImageFileNameUrsula()));                case "Flounder" -> PlayerImage=new Image(new FileInputStream(getImageFileNameFlounder()));                case "Sebastian"-> PlayerImage=new Image(new FileInputStream(getImageFileNameSebastian()));        }        double x = getPlayerCol() * cellWidth; //change by each cell size        double y = getPlayerRow() * cellHeight;        graphicsContext.setFill(Color.GREEN);        if(PlayerImage == null){            graphicsContext.fillRect(x, y, cellWidth, cellHeight);        }        else{            graphicsContext.drawImage(PlayerImage, x, y, cellWidth, cellHeight);        }    } catch (FileNotFoundException e) {            ErrorMessage("Error occurred while drawing maze player function");        }    }        private void drawMazeStartAndGoal(GraphicsContext graphicsContext, double cellHeight, double cellWidth) {        Image StartImage = null;        Image GoalImage = null;        try {            StartImage = new Image(new FileInputStream(getImageFileNameStartPosition()));        } catch (FileNotFoundException e) {            ErrorMessage("There is no Start image");        }        try {            GoalImage = new Image(new FileInputStream(getImageFileNameGoalPosition()));        } catch (FileNotFoundException e) {            ErrorMessage("There is no Goal image");        }        double x_Start = mazeDisplay.getStartPosition().getColumnIndex() * cellWidth;        double y_Start = mazeDisplay.getStartPosition().getRowIndex() * cellHeight;        double x_Goal = mazeDisplay.getGoalPosition().getColumnIndex() * cellWidth;        double y_Goal = mazeDisplay.getGoalPosition().getRowIndex() * cellHeight;        graphicsContext.setFill(Color.BLUEVIOLET); //the color that we want to add in our draw        if((mazeDisplay.getStartPosition().getRowIndex() != this.PlayerRow) || (mazeDisplay.getStartPosition().getColumnIndex() != this.PlayerCol))        {            if(StartImage== null)                graphicsContext.fillRect(x_Start, y_Start, cellWidth, cellHeight);            else                graphicsContext.drawImage(StartImage, x_Start, y_Start, cellWidth, cellHeight);        }        graphicsContext.setFill(Color.GREENYELLOW); //the color that we want to add in our draw        if((mazeDisplay.getGoalPosition().getRowIndex() != this.PlayerRow) || (mazeDisplay.getGoalPosition().getColumnIndex() != this.PlayerCol))        {            if(GoalImage== null)                graphicsContext.fillRect(x_Goal, y_Goal, cellWidth, cellHeight);            else                graphicsContext.drawImage(GoalImage, x_Goal, y_Goal, cellWidth, cellHeight);        }    }    private void drawMazeWalls(GraphicsContext graphicsContext, int rows, int cols, double cellHeight, double cellWidth) {        Image selected = null;        graphicsContext.setFill(Color.BLUE); //the color that we want to add in our draw        int counter = 0;        //iterate over all cells, if the cell value = 1, fill the cell with BLUE color        for (int i = 0; i < rows; i++) {            for (int j = 0; j < cols; j++) {                if(mazeDisplay.getMazeContent()[i][j] == 1){                    //if it is a wall:                    double x = j * cellWidth;                    double y = i * cellHeight;                    try {                        switch (counter) {                            case 0 -> selected = new Image(new FileInputStream(getImageFileNameWall()));                            case 1 -> selected = new Image(new FileInputStream(getImageFileNameWall2()));                            case 2 -> selected = new Image(new FileInputStream(getImageFileNameWall3()));                            case 3 -> selected = new Image(new FileInputStream(getImageFileNameWall4()));                            case 4 -> {                                selected = new Image(new FileInputStream(getImageFileNameWall5()));                                counter = -1;}}                    } catch (FileNotFoundException e) {                        ErrorMessage("There is no wall image");}                    counter++;                    if(selected== null)                        graphicsContext.fillRect(x+(cellWidth/4), y+(cellHeight/4), cellWidth/2, cellHeight/2);                    else                        graphicsContext.drawImage(selected, x+(cellWidth/4), y+(cellHeight/4), cellWidth/2, cellHeight/2);                }}}}    public void drawMaze(){        draw();    }    /*----------    Alert    ----------*/    private void InformationMessage(String message){        Alert alert = new Alert(Alert.AlertType.INFORMATION);        alert.setContentText(message);        alert.show(); }    private void ErrorMessage(String message){        Alert alert = new Alert(Alert.AlertType.ERROR);        alert.setContentText(message);        alert.show(); }    //getters & setters    public void setPlayer(String player) { this.player = player;}    public void setMazeDisplay(Maze mazeDisplay) {        this.mazeDisplay = mazeDisplay;}    public void deleteSolution() {        this.solution = null;    }    public Solution getSolution() {        return solution;    }    public Boolean getDrawSolution() {        return drawSolution;    }    public void setImageFileNameStartPosition(String imageFileNameStartPosition) {this.imageFileNameStartPosition.set(imageFileNameStartPosition);}    public String getImageFileNameFlounder() {return imageFileNameFlounder.get();}    public String getImageFileNameSebastian() { return imageFileNameSebastian.get();}    public void setImageFileNameSebastian(String imageFileNameSebastian) { this.imageFileNameSebastian.set(imageFileNameSebastian);}    public String getImageFileNameWall() {        return imageFileNameWall.get();    }    public void setImageFileNameWall(String imageFileNameWall) {        this.imageFileNameWall.set(imageFileNameWall);    }    public String getImageFileNameStartPosition() {        return imageFileNameStartPosition.get();    }    public String getImageFileNameGoalPosition() {        return imageFileNameGoalPosition.get();    }    public String getImageFileNameSolution() {        return imageFileNameSolution.get();    }    public void setImageFileNameSolution(String imageFileNameSolution) { this.imageFileNameSolution.set(imageFileNameSolution);}    public String getImageFileNameWall3() {        return imageFileNameWall3.get();    }    public void setImageFileNameWall3(String imageFileNameWall3) {        this.imageFileNameWall3.set(imageFileNameWall3);    }    public String getImageFileNameWall4() {        return imageFileNameWall4.get();    }    public void setImageFileNameWall4(String imageFileNameWall4) {        this.imageFileNameWall4.set(imageFileNameWall4);    }    public void setImageFileNameFlounder(String imageFileNameFlounder) {this.imageFileNameFlounder.set(imageFileNameFlounder);}    public String getImageFileNameUrsula() {return imageFileNameUrsula.get();}    public void setImageFileNameUrsula(String imageFileNameUrsula) { this.imageFileNameUrsula.set(imageFileNameUrsula);}    public void setImageFileNameGoalPosition(String imageFileNameGoalPosition) {this.imageFileNameGoalPosition.set(imageFileNameGoalPosition);}    public String getImageFileNameAriel() {return imageFileNameAriel.get();}    public void setImageFileNameAriel(String imageFileNameAriel) { this.imageFileNameAriel.set(imageFileNameAriel);}    public String getImageFileNameBackground() {        return imageFileNameBackground.get();    }    public void setImageFileNameBackground(String imageFileNameBackground) { this.imageFileNameBackground.set(imageFileNameBackground);}    public String getImageFileNameWall2() {        return imageFileNameWall2.get();    }    public String getImageFileNameWall5() {        return imageFileNameWall5.get();    }    public void setImageFileNameWall5(String imageFileNameWall5) {        this.imageFileNameWall5.set(imageFileNameWall5);    }    public void setImageFileNameWall2(String imageFileNameWall2) {        this.imageFileNameWall2.set(imageFileNameWall2);    }    public int getPlayerRow() {        return PlayerRow;    }    public int getPlayerCol() {        return PlayerCol;    }}